var num1 = '',
    num2 = ''
// console.log( num1 + num2 )
// 大数怎么相加?? 数字超过语言的计算范围
// 在内存中表达，数字型已经不行了 Infinity ,所以每次取出最后一位相加，
function addTwoNumber(str1, str2){
    // 1.字符串从尾部到首部
    // 2.两个数的位数不一样
    var carry = 0,//进位 
        l1 = str1.length,
        l2 = str2.length,
        arr = [], //放结果
        max = Math.max(l1,l2)//不一样长
    // n 最多计算多少次
    for (var i =l1-1, j = l2-1, n = max - 1; n >= 0;n--, i--, j--){
        var sum = (+str1[i] || 0) + (+str2[j] || 0) + carry //前面加+，说明要进行加法了，也可以使用parseInt
        // || 0 说明如果没有值，则为0
        if(sum>=10){
            carry = 1
            arr.push(sum-10)
        }
        else{
            carry = 0
            arr.push(sum)
        }
    }
    if(carry > 0) arr.push(carry);
    return arr.reverse().join('')
}
console.log(addTwoNumber(num1, num2))